const hamburger = document.querySelector('.hamburger'), menu = document.querySelector('.menu'), closeElem = document.querySelector('.menu__close'); hamburger.addEventListener('click', () =>{ menu.classList.add('active'); }); closeElem.addEventListener('click', () =>{ menu.classList.remove('active'); }); const countres = document.querySelectorAll('.skills__ratings-counter'), lines = document.querySelectorAll('.skills__ratings-line span'); countres.forEach( (item, i) => { lines[i].getElementsByClassName.width = item.innerHTML; });